style_sheet:
QMainWindow { background-color: #2e2e2e; border: 2px solid #555555; border-radius: 5px; }
    QWidget#dialog_container { background-color: #3c3c3c; border: 2px solid #666666; border-radius: 8px; padding: 10px; }
    QLabel#characterNameLabel { color: #FFD700; font-size: 24px; padding-bottom: 5px; border-bottom: 2px solid #888888; margin-bottom: 15px; }
    QTextEdit#dialogTextDisplay { background-color: #282828; color: #E0E0E0; border: 1px solid #505050; border-radius: 5px; padding: 10px; selection-background-color: #6a6a6a; }
    QTextEdit#userInputLine { background-color: #383838; color: #E0E0E0; border: 1px solid #505050; border-radius: 5px; padding: 8px; }
    QPushButton#actionButton { background-color: #007ACC; color: white; border: none; border-radius: 8px; padding: 10px 20px; margin-top: 10px; }
    QPushButton#actionButton:hover { background-color: #005f99; }
    QPushButton#actionButton:pressed { background-color: #004c80; }
    QPushButton#actionButton:disabled { background-color: #808080; color: #C0C0C0; }
    QTextEdit {
        border: 1px solid #cccccc; /* QTextEdit本身的边框 */
    }

    QTextEdit QScrollBar:vertical {
        border: none; /* 垂直滚动条无边框 */
        background: transparent; /* 背景透明 */
        width: 10px; /* 滚动条宽度 */
        margin: 0px 0px 0px 0px;
    }

    QTextEdit QScrollBar::handle:vertical {
        background: #888888; /* 滑动条手柄为灰色 */
        min-height: 20px;
        border-radius: 5px; /* 圆角 */
    }

    QTextEdit QScrollBar::add-line:vertical {
        border: none;
        background: none;
    }

    QTextEdit QScrollBar::sub-line:vertical {
        border: none;
        background: none;
    }

    QTextEdit QScrollBar::add-page:vertical, QTextEdit QScrollBar::sub-page:vertical {
        background: none; /* 滚动条空白区域透明 */
    }

    QTextEdit QScrollBar:horizontal {
        border: none; /* 水平滚动条无边框 */
        background: transparent; /* 背景透明 */
        height: 10px; /* 滚动条高度 */
        margin: 0px 0px 0px 0px;
    }

    QTextEdit QScrollBar::handle:horizontal {
        background: #888888; /* 滑动条手柄为灰色 */
        min-width: 20px;
        border-radius: 5px; /* 圆角 */
    }

    QTextEdit QScrollBar::add-line:horizontal {
        border: none;
        background: none;
    }

    QTextEdit QScrollBar::sub-line:horizontal {
        border: none;
        background: none;
    }

    QTextEdit QScrollBar::add-page:horizontal, QTextEdit QScrollBar::sub-page:horizontal {
        background: none; /* 滚动条空白区域透明 */
    }
    QPushButton {
        background-color: transparent; /* 默认背景透明 */
        border: none; /* 默认无边框 */
    }
    QPushButton:hover {
        background-color: #666666;
        color: #FFFFFF;
        border: 1px solid #888888;
        border-radius: 5px;
        padding: 5px 10px;
    }
    QPushButton:pressed {
        background-color: #555555;
    }
    QScrollArea {
        background-color: transparent; /* 将 QScrollArea 自身背景设置为透明 */
        border: none;
    }
    QWidget#scrollAreaContent { /* 为 QScrollArea 的内容容器设置背景颜色 */
        background-color: #3c3c3c;
    }
    QScrollArea QScrollBar:vertical {
        border: none; /* 垂直滚动条无边框 */
        background: transparent; /* 背景透明 */
        width: 0px; /* 滚动条宽度 */
        margin: 0px 0px 0px 0px;
    }

    QScrollArea QScrollBar::handle:vertical {
        background: #888888; /* 滑动条手柄为灰色 */
        min-height: 20px;
        border-radius: 5px; /* 圆角 */
    }

    QScrollArea QScrollBar::add-line:vertical {
        border: none;
        background: none;
    }

    QScrollArea QScrollBar::sub-line:vertical {
        border: none;
        background: none;
    }

    QScrollArea QScrollBar::add-page:vertical, QTextEdit QScrollBar::sub-page:vertical {
        background: none; /* 滚动条空白区域透明 */
    }

    QScrollArea QScrollBar:horizontal {
        border: none; /* 水平滚动条无边框 */
        background: transparent; /* 背景透明 */
        height: 0px; /* 滚动条高度 */
        margin: 0px 0px 0px 0px;
    }

    QScrollArea QScrollBar::handle:horizontal {
        background: #888888; /* 滑动条手柄为灰色 */
        min-width: 20px;
        border-radius: 5px; /* 圆角 */
    }

    QScrollArea QScrollBar::add-line:horizontal {
        border: none;
        background: none;
    }

    QScrollArea QScrollBar::sub-line:horizontal {
        border: none;
        background: none;
    }

    QScrollArea QScrollBar::add-page:horizontal, QTextEdit QScrollBar::sub-page:horizontal {
        background: none; /* 滚动条空白区域透明 */
    }
